import React from 'react';
import { Component } from 'react';  
import './singlePlatForm.css'
import { Media } from 'reactstrap';
import Axios from "axios";
// const places = 
// 	[
// 		{
// 			id: 0,
// 			name: '1',
// 			image: './classicMusic.jpg',
// 			owner: 'A',
// 			discrpiton:'abababaaba',
//             rate:1000,
// 			hot:10
// 		} ,
// 		{
// 			id: 1,
// 			name: '122',
// 			image: './classicMusic.jpg',
// 			owner: 'A',
// 			discrpiton:'abababaaba',
//             rate:10,
// 			hot:5
