import React from 'react';
import { Component } from 'react';  
import { Link } from 'react-router-dom';

import { Media } from 'reactstrap';
import './EditPlatform.css'; 
import Axios from "axios";  


    export default class EditPlatform extends Component {
        constructor(props) {
            super(props);
            this.state = { 
              trigger:false,   
              name:"",
              tag:"",
              namechange:false,
              tagchange:false,
              oneOnwer:false,
              requireReputation:0,
              repchange:false,
              EPID:localStorage.getItem('EPID'),
              success:false
            };    
          }
        handleChangeName(e) {  
            this.setState({name: e.target.value}); 
            this.setState({namechange:true});  
        }
     
        handleChangeTag(e) {  
        this.setState({tag: e.target.value});  
        this.setState({tagchange:true}); 
        }

        handleChangeOnwer(e)   {
            this.setState({oneOnwer: e.target.value}); 
            if (e.target.value){
              this.setState({replimit:99999});
              this.setState({repchange:true}); 
            }    
        }

        handleReputation(e){
            this.setState({requireReputation: e.target.value}); 
            this.setState({repchange:true});  
        }

       update


